;(function() {

    var $posts = $('div#posts'),
        $searchToggle = $('a#search-toggle'),
        $searchForm = $('form#search-form');

    $posts.fitVids();

    $('.photoset-grid').photosetGrid({
        highresLinks: true,
        rel: $('.photoset-grid').attr('data-id'),
        gutter: '5px'
    });

    if (UltraFlow.isIndexPage) {
        $posts.imagesLoaded(function () {
            $posts.masonry({
                itemSelector: 'article.post',
                columnWidth: 500,
                gutter: 30
            });
        });
    }

    if (UltraFlow.infiniteScroll && UltraFlow.isIndexPage) {
        $posts.infinitescroll({
            navSelector: 'nav.posts-nav',
            nextSelector: 'a.btn--next',
            itemSelector: 'article.post',
            loading: {
                selector: $('div.infinite-scroll'),
                img: 'data:image/gif;base64,',
                msgText: '',
                finishedMsg: '<em>There are no more posts.</em>'
            }
        }, function (json, opts) {
            $posts.fitVids();

            $('.photoset-grid').photosetGrid({
                highresLinks: true,
                rel: $('.photoset-grid').attr('data-id'),
                gutter: '5px'
            });

            $posts.imagesLoaded(function () {
                var $newPosts = $(json);
                $posts.masonry('appended', $newPosts);
            });

            // Lightbox for photoset grids
            $('.photoset-grid').each(function() {
                $(this).magnificPopup({
                    delegate: 'a', // the container for each your gallery items
                    type: 'image',
                    gallery: { enabled: true },
                    removalDelay: 300, // delay in milliseconds before popup is removed
                    mainClass: 'mfp-fade' // class that is added to popup wrapper and background
                });
            });

            Tumblr.LikeButton.get_status_by_page(opts.state.currPage);
        });
    }

    $searchToggle.on('click', function (e) {
        e.preventDefault();

        $searchForm.fadeToggle(200);
    });

    // Lightbox for photoset grids
    $('.photoset-grid').each(function() {
        $(this).magnificPopup({
            delegate: 'a', // the container for each your gallery items
            type: 'image',
            gallery: { enabled: true },
            removalDelay: 300, // delay in milliseconds before popup is removed
            mainClass: 'mfp-fade' // class that is added to popup wrapper and background
        });
    });

})();
